                                variant="h6"
                            >
                                MealsFromDirt
                            </Typography>  
                        </Box>

                        <Box>
                            <TextField 
                                id="outlined-basic" 
                                label="Address" 
                                variant="outlined"
                                margin="normal"
                                InputLabelProps={{ style:{ color: 'white' } }}
                            /> 
                        </Box>

                        <FormControlLabel
