import { Fragment, useState } from "react";
import Box from "@mui/material/Box";
import AppBar from '@mui/material/AppBar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import HelpIcon from '@mui/icons-material/Help';
import Toolbar from '@mui/material/Toolbar';
import Switch from '@mui/material/Switch';
import { FormControlLabel, Popover, TextField } from "@mui/material";
import switchConfig from "./configuration";
import AboutUs from "./AboutPopUp";

function TopBar(props) { 
    const {menuType} = props;
    const {setMenuType} = props;

    const handleCardSwitch = () => {
        console.log(menuType);
        if(menuType.type === 'Crop'){
            setMenuType({type: 'Meal'})
        }else if(menuType.type === 'Meal'){
            setMenuType({type: 'Crop'})
        }
    }

    const handleAboutButton = () => {
        return(
            <Popover
                anchorReference="anchorPosition"
                anchorPosition={{ top: 100, left: 100 }}
                anchorOrigin={{
                    vertical: 'center',
                    horizontal: 'center',
                }}
                transformOrigin={{
                    vertical: 'center',
                    horizontal: 'center',
                }}
            >
                {/* <AboutUs/> */}
                About us
            </Popover>
        )
    }

    return(
        <Fragment>
            <Box>
                <AppBar 
                    position="static" 
                    sx={{
                        backgroundColor: 'rgb(50,53,60)'
                    }} 
                >
                    <Toolbar>
                        <Box
                            align='left'
                            sx={{width:140}}
                        >
                            {/* Possibly change to image of logo instead */}
                            <Typography
                                variant="h6"
                            >
                                MealsFromDirt
                            </Typography>  
                        </Box>

                        {/* Enter Address here */}
                        <Box>
                            <TextField 
                                id="outlined-basic" 
                                label="Address" 
                                variant="outlined"
                                margin="normal"
                                InputLabelProps={{ style:{ color: 'white' } }}
                            /> 
                        </Box>

                        {/* Crop/Menu Switch */}
                        <FormControlLabel
                            value="bottom"
                            control={
                                <Switch
                                    menuType={menuType}
                                    onChange={handleCardSwitch}
                                />
                            }
                            label={menuType.type}
                            labelPlacement="bottom"
                        />

                        {/* About Us Button */}
                        <IconButton 
                            color='primary' 
                            onclick={handleAboutButton}
                        > 
                            <HelpIcon fontSize='large' />
                        </IconButton>
                        <Popover
                anchorReference="anchorPosition"
                anchorPosition={{ top: 100, left: 100 }}
                anchorOrigin={{
                    vertical: 'center',
                    horizontal: 'center',
                }}
                transformOrigin={{
                    vertical: 'center',
                    horizontal: 'center',
                }}
            >
                {/* <AboutUs/> */}
                About us
            </Popover>

                    </Toolbar>   
                </AppBar>
            </Box>
        </Fragment>
    )
}

export default TopBar;
